// ghrp\.kr\.sec\.samsung\.net

workTime: {
    let workTimeMsg = ""

    //----------------------------------------------------- Util

    function time2min(time) {
        let absTime = time.replace(/ |-/g,"")
        let absMin = +absTime.split(":")[0]*60 + +absTime.split(":")[1]

        if ( time.includes("-") ) { return -absMin }
        else { return absMin }
    }

    function min2time(min) {
        // let absMin = min >= 0 ? min : -min
        let absMin = min.toString().replace(/ |-/g,"")
        let absTime = Math.floor(+absMin / 60) + ":" + (+absMin % 60).toString().padStart(2, "0")
        if ( min.toString().includes("-") ) { return "-" + absTime }
        else { return absTime }
    }

    function getRestTime(workMin) {
        let restMin = Math.floor(+workMin / time2min("4:30")) * 30
        return (restMin > 60 ? 60 : restMin)
    }

    function getWorkWithRestTime(workMin) {
        let restMinNeed = 0
        if (+workMin >= (8*60)) { restMinNeed = 60 }
        else if (+workMin >= (4*60)) { restMinNeed = 30 }
        return +workMin + restMinNeed
    }

    //----------------------------------------------------- Main

    function updateWorkTime() {

        // Get local time
        let date = new Date()
        // let currentDate = date.toISOString().split("T")[0] // for utc time
        let currentDate = date.getFullYear() + "-" + (date.getMonth()+1).toString().padStart(2, "0") + "-" + date.getDate().toString().padStart(2, "0")  // for local time
        let currentTime = +date.getHours()*60 + +date.getMinutes()


        // Get GHRP data
        let GHRP_monthMinWorkTime           = time2min( $("dt:contains('ì›” í•„ìˆ˜ê·¼ë¬´ ì‹œê°„') ~ * span:contains('ê¸°ì¤€')").next().text() )
        let GHRP_monthMinWorkTime_worked    = time2min( $("dt:contains('ì›” í•„ìˆ˜ê·¼ë¬´ ì‹œê°„') ~ * span:contains('ê·¼ë¬´')").next().text() )
        let GHRP_monthMinWorkTime_necessary = time2min( $("dt:contains('ì›” í•„ìˆ˜ê·¼ë¬´ ì‹œê°„') ~ * span:contains('í•„ìš”')").next().text() )
        let GHRP_monthMaxWorkTime_available = time2min( $("dt:contains('ì›” ìµœëŒ€ê·¼ë¬´ ì‹œê°„') ~ * span:contains('ê°€ëŠ¥')").next().text() )

        let GHRP_overWorkTime               = time2min( $("dt:contains('í‰ì¼ ëˆ„ì  ì´ˆê³¼ê·¼ë¬´ì‹œê°„')").next().text() )
        let GHRP_maxWorkTimePerDay          = time2min( $("dt:contains('ì¼í‰ê·  ìµœëŒ€ê·¼ë¬´ê°€ëŠ¥ì‹œê°„')").next().text() )

        // Calculate more information from GHRP data
        let GHRP_C_monthMinWorkTime_necessary = GHRP_monthMinWorkTime - GHRP_monthMinWorkTime_worked // "í•„ìš”"ëŠ” 0 ë°‘ìœ¼ë¡œ ë–¨ì–´ì§€ì§€ ì•Šìœ¼ë¯€ë¡œ ë³„ë„ ê³„ì‚°

        let GHRP_C_leftWorkDay_total = (GHRP_maxWorkTimePerDay == 0) ? 1 : Math.round( GHRP_monthMaxWorkTime_available / GHRP_maxWorkTimePerDay )
        let GHRP_C_leftWorkDay_real  = Math.ceil( (GHRP_C_monthMinWorkTime_necessary + GHRP_overWorkTime) / (8*60) )

        let GHRP_C_minWorkTimePerDay = Math.round( GHRP_monthMinWorkTime_necessary / GHRP_C_leftWorkDay_real )


        // TODAY : Get GHRP data
        let GHRP_todayCell = $("button:contains('" + currentDate + "')").parent().parent().parent()

        let GHRP_inTime_RAW          = GHRP_todayCell.next().next().text()
        let GHRP_inTime_modified_RAW = GHRP_todayCell.next().next().next().text()

        let GHRP_exceptTime = time2min( GHRP_todayCell.next().next().next().next().next().next().next().next().next().next().next().text() )


        // TODAY : Calculate more information from GHRP data
        let GHRP_C_inTime_RAW = (GHRP_inTime_modified_RAW == "") ? GHRP_inTime_RAW : GHRP_inTime_modified_RAW


        // TODAY : Generate informations from data
        let altInTime   = time2min( (GHRP_C_inTime_RAW == "-") ? min2time(currentTime) : GHRP_C_inTime_RAW )

        let stayTime    = +currentTime - altInTime
        let restTime    = getRestTime(stayTime - GHRP_exceptTime)
        let workTime    = (stayTime - GHRP_exceptTime) - restTime

        let todayOutTime_8hr        = altInTime + getWorkWithRestTime(8*60) + GHRP_exceptTime
        let todayOutTime_necessary  = altInTime + getWorkWithRestTime(GHRP_C_minWorkTimePerDay) + GHRP_exceptTime

        let todayLeftWorkTime_8hr       = todayOutTime_8hr - currentTime
        let todayLeftWorkTime_necessary = todayOutTime_necessary - currentTime

/// ì—¬ê¸°ê¹Œì§€ ìˆ˜ì •í•¨

        let totalLeftMonthMiniMin   = GHRP_monthMinWorkTime_necessary - workTime
        // let totalLeftOverMin_8hr    = ((GHRP_C_leftWorkDay_total - 1) * (8*60)) - totalLeftMonthMiniMin
        let totalLeftOverMin_8hr    = ((GHRP_C_leftWorkDay_real - 1) * (8*60)) - totalLeftMonthMiniMin

// alert("â²ï¸â­â„âš¡ğŸ”¥â±ğŸ•°â°âŒšâ³âŒ›ğŸ“ŒğŸ“ğŸ’°ğŸ’¸ğŸ“…ğŸ“†ğŸ—“ğŸ˜¤ğŸ˜ğŸ˜¥ğŸ§­ğŸ’ªğŸ™ğŸ©ºğŸ‰ğŸ®âš–")


        /////////////////////////////////////////////////////////////////////////////////////// DISPLAY area

        // initial area
        document.getElementById("wt-now").textContent = min2time(currentTime)
        if (GHRP_todayCell.text().includes(currentDate)) {
