        }
        else {
            document.getElementById("wt-date-warn").style.display = "block"
            return
        }

        // ğŸ“… date area
        document.getElementById("wt-date").textContent = currentDate
        document.getElementById("wt-intime").textContent = min2time(altInTime)
        if (GHRP_C_inTime_RAW == "-") {
            document.getElementById("wt-popup-intime-info").style.display = ""
        }
        else {
            document.getElementById("wt-popup-intime-info").style.display = "none"
        }
        // document.getElementById("wt-workday-left").textContent = GHRP_C_leftWorkDay_total
        document.getElementById("wt-workday-left").textContent = GHRP_C_leftWorkDay_real
        // $("#wt_date").html(currentDate)


        // ğŸ“Œì˜¤ëŠ˜ì˜ ê·¼ë¬´ì‹œê°„
        document.getElementById("wt-today-worktime").textContent = min2time(workTime)
        document.getElementById("wt-today-worktime-info").textContent = "(ì²´ë¥˜ : " + min2time(stayTime) + ", íœ´ê²Œ : " + min2time(restTime) + ", ì œì™¸ : " + min2time(GHRP_exceptTime) + ")"

        document.getElementById("wt-worktime-left-today-8hr-endtime").textContent = min2time(todayOutTime_8hr)
        document.getElementById("wt-worktime-left-today-8hr-resttime").textContent = min2time(getRestTime(getWorkWithRestTime(8*60)))
        document.getElementById("wt-worktime-left-today-8hr").textContent = min2time(todayLeftWorkTime_8hr)
        if (todayLeftWorkTime_8hr <= 0) { document.getElementById("wt-worktime-left-today-8hr-message").textContent = "ğŸ˜¤ ì–´ì„œ í‡´ê·¼í•˜ì„¸ìš”!!" }
        else if (todayLeftWorkTime_8hr <= 60) { document.getElementById("wt-worktime-left-today-8hr-message").textContent = "ğŸ˜ ê³§ í‡´ê·¼í•´ë„ ì¢‹ì•„ìš”~" }
        else { document.getElementById("wt-worktime-left-today-8hr-message").textContent = "ğŸ˜¥ í‡´ê·¼ê¹Œì§€ í™”ì´íŒ…!" }

        document.getElementById("wt-worktime-left-per-day").textContent = min2time(GHRP_C_minWorkTimePerDay)
        document.getElementById("wt-worktime-left-today-real-endtime").textContent = min2time(todayOutTime_necessary)
        document.getElementById("wt-worktime-left-today-real-resttime").textContent = min2time(getRestTime(getWorkWithRestTime(GHRP_C_minWorkTimePerDay)))
        document.getElementById("wt-worktime-left-today-real").textContent = min2time(todayLeftWorkTime_necessary)



        // ğŸ“Œì–´ì œê¹Œì§€ ëˆ„ì  ì´ˆê³¼ ê·¼ë¬´
        document.getElementById("wt-overtime").textContent = min2time(GHRP_overWorkTime)

        document.getElementById("wt-overtime-left-8hr").textContent = min2time(totalLeftOverMin_8hr)
        if (totalLeftOverMin_8hr <= 0) { document.getElementById("wt-overtime-left-8hr-message").textContent = "â³ ê·¼ë¬´ì‹œê°„ì´ ë¶€ì¡±í•´ìš” ã… ã… " }
        else if (totalLeftOverMin_8hr <= (20*60)) { document.getElementById("wt-overtime-left-8hr-message").textContent = "ğŸ’¸ í¬ê´„ì„ê¸ˆ ì˜ì—­ì…ë‹ˆë‹¤." }
        else { document.getElementById("wt-overtime-left-8hr-message").textContent = "ğŸ’° ì´ˆê³¼ê·¼ë¬´ ì¤‘ì…ë‹ˆë‹¤." }

        if (GHRP_C_leftWorkDay_real > 1) {
            document.getElementById("wt-overtime-left-per-day").textContent = min2time( Math.round( totalLeftMonthMiniMin / ( GHRP_C_leftWorkDay_real - 1 ) ) )
            document.getElementById("wt-popup-expectedLeftTimePerDay-info").style.display = "none"
        }
        else {
            document.getElementById("wt-overtime-left-per-day").textContent = "- "
            document.getElementById("wt-popup-expectedLeftTimePerDay-info").style.display = ""
        }


    }


    //----------------------------------------------------- Interface

    function openForm() {
        updateWorkTime()
        document.getElementById("myForm").style.display = "block";
    }

    function closeForm() {
        document.getElementById("myForm").style.display = "none";
    }

    function togglePopupButton(buttonId) {
        let button      = document.getElementById(buttonId);
        let text        = button.querySelector(".wt-popup-text")
        let arrow       = button.querySelector(".wt-popup-arrow")
        let textRect    = text.getBoundingClientRect()

        if ( (textRect.width / 2) < textRect.left ) {
            text.style.marginLeft = - (textRect.width / 2) + "px"
        }
        else {
            text.style.marginLeft = - (textRect.left) + "px"
        }
        // popup.style.margin="0 0 0 -" + (popup.clientWidth / 2) + "px"
        text.classList.toggle("show");
        arrow.classList.toggle("show");
    }


    //----------------------------------------------------- Init

    function autoUpdate() {
        updateWorkTime()
        setTimeout(autoUpdate, 1000 * 60); // 1min
    }

    function initWorkTime() {
        document.getElementById("openbutton").style.display = "block";
        autoUpdate()
        openForm()
    }

    //----------------------------------------------------- Trigger

    function checkWorkTimePage() {
        let menuTitle=document.querySelector("#etcMenuTitle")

        if (menuTitle !== null) {
